<?php
// /detail_produk.php (Versi Final dengan SEMUA FITUR)
require_once 'config/database.php';
require_once 'includes/functions.php';

// Fungsi untuk menyensor nama reviewer
function sensorNama($nama) {
    $parts = explode(' ', $nama);
        $nama_sensor = '';
            foreach ($parts as $part) {
                    if (strlen($part) > 2) {
                                $nama_sensor .= strtoupper(substr($part, 0, 1)) . str_repeat('*', strlen($part) - 2) . strtolower(substr($part, -1)) . ' ';
                                        } else {
                                                    $nama_sensor .= strtoupper(substr($part, 0, 1)) . '* ';
                                                            }
                                                                }
                                                                    return trim($nama_sensor);
                                                                    }

                                                                    if (!isset($_GET['id']) || empty($_GET['id'])) {
                                                                        header("Location: index.php");
                                                                            exit();
                                                                            }
                                                                            $produk_id = (int)$_GET['id'];

                                                                            // Mengambil semua data produk, termasuk 3 kolom gambar
                                                                            $stmt_produk = $koneksi->prepare("
                                                                                SELECT p.*, t.nama_toko, u.id as user_id_penjual 
                                                                                    FROM produk p 
                                                                                        JOIN toko t ON p.toko_id = t.id 
                                                                                            JOIN users u ON t.user_id = u.id
                                                                                                WHERE p.id = ? AND p.status_moderasi = 'disetujui' AND t.is_active = 1
                                                                                                ");
                                                                                                $stmt_produk->bind_param("i", $produk_id);
                                                                                                $stmt_produk->execute();
                                                                                                $result_produk = $stmt_produk->get_result();
                                                                                                if ($result_produk->num_rows !== 1) {
                                                                                                    echo "<div class='container mt-4'><div class='alert alert-danger'>Produk tidak ditemukan, belum disetujui, atau toko sedang tidak aktif.</div></div>";
                                                                                                        exit();
                                                                                                        }
                                                                                                        $produk = $result_produk->fetch_assoc();
                                                                                                        $stmt_produk->close();

                                                                                                        $page_title = htmlspecialchars($produk['nama_produk']);
                                                                                                        require_once 'includes/header.php';

                                                                                                        $promo_data = getEffectivePriceAndPromoStatus($produk);

                                                                                                        // Ambil total produk terjual
                                                                                                        $stmt_terjual = $koneksi->prepare("
                                                                                                            SELECT SUM(dp.jumlah) as total 
                                                                                                                FROM detail_pesanan dp
                                                                                                                    JOIN pesanan p ON dp.pesanan_id = p.id
                                                                                                                        WHERE dp.produk_id = ? AND p.status_pesanan = 'selesai'
                                                                                                                        ");
                                                                                                                        $stmt_terjual->bind_param("i", $produk_id);
                                                                                                                        $stmt_terjual->execute();
                                                                                                                        $total_terjual = $stmt_terjual->get_result()->fetch_assoc()['total'] ?? 0;
                                                                                                                        $stmt_terjual->close();

                                                                                                                        // Kumpulkan semua gambar yang ada ke dalam satu array
                                                                                                                        $product_images = [];
                                                                                                                        if (!empty($produk['gambar_produk'])) $product_images[] = $produk['gambar_produk'];
                                                                                                                        if (!empty($produk['gambar_produk_2'])) $product_images[] = $produk['gambar_produk_2'];
                                                                                                                        if (!empty($produk['gambar_produk_3'])) $product_images[] = $produk['gambar_produk_3'];
                                                                                                                        ?>

                                                                                                                        <div class="mb-3">
                                                                                                                            <a href="#" onclick="history.back(); return false;" class="text-dark text-decoration-none fs-4">
                                                                                                                                    <i class="bi bi-arrow-left-circle"></i>
                                                                                                                                        </a>
                                                                                                                                        </div>

                                                                                                                                        <div id="notification-container" style="position: fixed; top: 80px; right: 20px; z-index: 1050; width: 300px;"></div>

                                                                                                                                        <div class="row">
                                                                                                                                            <div class="col-md-6 mb-4">
                                                                                                                                                    <?php if (count($product_images) > 1): ?>
                                                                                                                                                                <div id="productCarousel" class="carousel slide shadow-sm rounded" data-bs-ride="carousel">
                                                                                                                                                                                <div class="carousel-indicators">
                                                                                                                                                                                                    <?php foreach ($product_images as $index => $image): ?>
                                                                                                                                                                                                                            <button type="button" data-bs-target="#productCarousel" data-bs-slide-to="<?php echo $index; ?>" class="<?php echo $index === 0 ? 'active' : ''; ?>" aria-current="true"></button>
                                                                                                                                                                                                                                                <?php endforeach; ?>
                                                                                                                                                                                                                                                                </div>
                                                                                                                                                                                                                                                                                <div class="carousel-inner rounded">
                                                                                                                                                                                                                                                                                                    <?php foreach ($product_images as $index => $image): ?>
                                                                                                                                                                                                                                                                                                                            <div class="carousel-item <?php echo $index === 0 ? 'active' : ''; ?>">
                                                                                                                                                                                                                                                                                                                                                        <img src="/uploads/produk/<?php echo htmlspecialchars($image); ?>" class="d-block w-100" alt="Gambar Produk <?php echo $index + 1; ?>" style="aspect-ratio: 1 / 1; object-fit: cover;">
                                                                                                                                                                                                                                                                                                                                                                                </div>
                                                                                                                                                                                                                                                                                                                                                                                                    <?php endforeach; ?>
