                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            <h2><?php echo htmlspecialchars($produk['nama_produk']); ?></h2>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    <p class="text-muted">Dijual oleh: <strong><?php echo htmlspecialchars($produk['nama_toko']); ?></strong></p>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            <p><small class="text-muted"><?php echo ($total_terjual > 0) ? "Telah terjual " . $total_terjual . " buah" : ""; ?></small></p>

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    <?php if ($promo_data['is_promo'] || !empty($promo_data['promo_text'])): ?>
