                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                $stmt_ulasan = $koneksi->prepare("SELECT u.*, us.nama_lengkap FROM ulasan u JOIN users us ON u.pembeli_id = us.id WHERE u.produk_id = ? ORDER BY u.created_at DESC");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $stmt_ulasan->bind_param("i", $produk_id);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                $stmt_ulasan->execute();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $result_ulasan = $stmt_ulasan->get_result();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $stmt_avg = $koneksi->prepare("SELECT AVG(rating) as avg_rating, COUNT(id) as total_ulasan FROM ulasan WHERE produk_id = ?");
