<?php
// /admin/detail_toko.php
$page_title = "Detail Toko";
require_once '../includes/header_admin.php';

// Validasi ID Toko dari URL
if (!isset($_GET['id']) || empty($_GET['id'])) {
    header("Location: kelola_toko.php");
        exit();
        }
        $toko_id = (int)$_GET['id'];

        // Ambil data utama toko dan pemiliknya
        $stmt_toko = $koneksi->prepare("
            SELECT t.*, u.username, u.nama_lengkap, u.email, u.created_at as tgl_daftar_user
                FROM toko t
                    JOIN users u ON t.user_id = u.id
                        WHERE t.id = ?
                        ");
                        $stmt_toko->bind_param("i", $toko_id);
                        $stmt_toko->execute();
                        $result_toko = $stmt_toko->get_result();

                        if ($result_toko->num_rows === 0) {
                            echo "<div class='alert alert-danger'>Toko tidak ditemukan.</div>";
                                require_once '../includes/footer_admin.php';
                                    exit();
                                    }
                                    $toko = $result_toko->fetch_assoc();
                                    $stmt_toko->close();

                                    // Ambil semua produk dari toko ini
                                    $stmt_produk = $koneksi->prepare("SELECT id, nama_produk, harga, stok, status_moderasi, gambar_produk FROM produk WHERE toko_id = ? ORDER BY created_at DESC");
                                    $stmt_produk->bind_param("i", $toko_id);
                                    $stmt_produk->execute();
                                    $result_produk = $stmt_produk->get_result();

                                    // Ambil riwayat keuangan toko
                                    $stmt_keuangan = $koneksi->prepare("SELECT * FROM riwayat_transaksi_penjual WHERE toko_id = ? ORDER BY tanggal_transaksi DESC LIMIT 20");
                                    $stmt_keuangan->bind_param("i", $toko_id);
                                    $stmt_keuangan->execute();
                                    $result_keuangan = $stmt_keuangan->get_result();

                                    ?>

