                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            p.*, 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            t.nama_toko,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            (SELECT AVG(rating) FROM ulasan WHERE produk_id = p.id) as avg_rating,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            (SELECT SUM(jumlah) FROM detail_pesanan WHERE produk_id = p.id) as total_terjual
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        FROM 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        produk p
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    JOIN 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    toko t ON p.toko_id = t.id
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                WHERE 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                p.status_moderasi = 'disetujui' AND t.is_active = 1
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ORDER BY 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            p.created_at DESC
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        LIMIT 12
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ";
