<?php
// /invoice.php (Versi Final dengan Jasa Kirim & Kode Unik)

require_once 'config/database.php';

if (!isset($_SESSION['user_id'])) {
    header("Location: /auth/login.php");
        exit();
        }

        $user_id = $_SESSION['user_id'];
        $pesanan_id = isset($_GET['id']) ? (int)$_GET['id'] : 0;

        if ($pesanan_id === 0) {
            die("Error: ID Pesanan tidak valid.");
            }

            $stmt = $koneksi->prepare("
                SELECT
                        p.*,
                                u.nama_lengkap AS nama_pembeli,
                                        u.email AS email_pembeli,
                                                p.no_telepon AS no_hp_pembeli,
                                                        t.nama_toko AS nama_penjual,
                                                                t.alamat_toko AS alamat_penjual
                                                                    FROM pesanan p
                                                                        JOIN users u ON p.pembeli_id = u.id
                                                                            LEFT JOIN (
                                                                                    SELECT dp.pesanan_id, tk.nama_toko, tk.alamat_toko
                                                                                            FROM detail_pesanan dp
                                                                                                    JOIN produk pr ON dp.produk_id = pr.id
                                                                                                            JOIN toko tk ON pr.toko_id = tk.id
                                                                                                                    WHERE dp.pesanan_id = ?
                                                                                                                            LIMIT 1
                                                                                                                                ) AS t ON p.id = t.pesanan_id
                                                                                                                                    WHERE p.id = ? AND p.pembeli_id = ?
                                                                                                                                    ");
                                                                                                                                    $stmt->bind_param("iii", $pesanan_id, $pesanan_id, $user_id);
                                                                                                                                    $stmt->execute();
                                                                                                                                    $pesanan = $stmt->get_result()->fetch_assoc();
                                                                                                                                    $stmt->close();

                                                                                                                                    if (!$pesanan) {
                                                                                                                                        die("Invoice tidak ditemukan atau Anda tidak memiliki akses.");
                                                                                                                                        }

                                                                                                                                        if ($pesanan['status_pesanan'] !== 'selesai') {
                                                                                                                                            $page_title = "Invoice Belum Tersedia";
                                                                                                                                                require_once 'includes/header.php';
                                                                                                                                                    echo "<div class='container my-5'><div class='alert alert-warning text-center'>Invoice baru akan tersedia setelah status pesanan Anda 'Selesai'.<br><a href='/detail_pesanan.php?id=$pesanan_id' class='btn btn-primary mt-3'>Kembali ke Detail Pesanan</a></div></div>";
                                                                                                                                                        require_once 'includes/footer.php';
                                                                                                                                                            exit();
                                                                                                                                                            }

                                                                                                                                                            $stmt_items = $koneksi->prepare("SELECT pr.nama_produk, pr.jenis_produk, dp.jumlah, dp.harga_satuan FROM detail_pesanan dp JOIN produk pr ON dp.produk_id = pr.id WHERE dp.pesanan_id = ?");
                                                                                                                                                            $stmt_items->bind_param("i", $pesanan_id);
                                                                                                                                                            $stmt_items->execute();
                                                                                                                                                            $items_result = $stmt_items->get_result();

                                                                                                                                                            $ada_produk_fisik = false;
                                                                                                                                                            $items_array = [];
                                                                                                                                                            while ($item = $items_result->fetch_assoc()) {
                                                                                                                                                                $items_array[] = $item;
                                                                                                                                                                    if ($item['jenis_produk'] == 'fisik') {
                                                                                                                                                                            $ada_produk_fisik = true;
                                                                                                                                                                                }
                                                                                                                                                                                }

                                                                                                                                                                                $jasa_kirim_map = [
                                                                                                                                                                                    'lokal' => 'Kurir Lokal',
                                                                                                                                                                                        'jnt'   => 'J&T Express',
                                                                                                                                                                                            'pos'   => 'POS Indonesia'
                                                                                                                                                                                            ];
                                                                                                                                                                                            $nama_jasa_kirim = $jasa_kirim_map[$pesanan['kurir']] ?? strtoupper(htmlspecialchars($pesanan['kurir'] ?? '-'));

                                                                                                                                                                                            $page_title = "Invoice #" . $pesanan['id'];
                                                                                                                                                                                            ?>
