// --- LOGIKA FILTER ---
$filter_status = isset($_GET['status']) ? $_GET['status'] : 'semua';
$where_clause = "";
// ---- BAGIAN PERUBAHAN UTAMA: Tambahkan 'pending' ke valid_statuses ----
$valid_statuses = ['menunggu_pembayaran', 'pending', 'diproses', 'dikirim', 'selesai', 'dibatalkan'];
// ---- AKHIR BAGIAN PERUBAHAN UTAMA ----

if (in_array($filter_status, $valid_statuses)) {
    $where_clause = "WHERE p.status_pesanan = ?";
    }

    // --- LOGIKA PAGINASI ---
    $halaman_aktif = isset($_GET['halaman']) ? (int)$_GET['halaman'] : 1;
    $limit = 15; // Jumlah pesanan per halaman
    $offset = ($halaman_aktif - 1) * $limit;

    // Query untuk menghitung total pesanan (untuk paginasi)
    $query_total = "SELECT COUNT(p.id) as total FROM pesanan p $where_clause";
    $stmt_total = $koneksi->prepare($query_total);
    if (!empty($where_clause)) {
        $stmt_total->bind_param("s", $filter_status);
        }
        $stmt_total->execute();
        $total_pesanan = $stmt_total->get_result()->fetch_assoc()['total'];
        $total_halaman = ceil($total_pesanan / $limit);
        $stmt_total->close();


        // Query untuk mengambil data pesanan
        $query = "
            SELECT p.*, u.username as nama_pembeli
                FROM pesanan p
                    JOIN users u ON p.pembeli_id = u.id
                        $where_clause
                            ORDER BY p.tanggal_pesanan DESC
                                LIMIT ? OFFSET ?
                                ";
                                $stmt = $koneksi->prepare($query);
                                if (!empty($where_clause)) {
                                    $stmt->bind_param("sii", $filter_status, $limit, $offset);
                                    } else {
                                        $stmt->bind_param("ii", $limit, $offset);
                                        }
                                        $stmt->execute();
                                        $result = $stmt->get_result();

                                        ?>

