<?php
// /penjual/laporan_keuangan.php (Versi Final Lengkap)
$page_title = "Laporan Keuangan Toko";
require_once '../includes/header_penjual.php';

// Ambil ID toko berdasarkan user_id session
$user_id = $_SESSION['user_id'];
$stmt_toko = $koneksi->prepare("SELECT id FROM toko WHERE user_id = ?");
$stmt_toko->bind_param("i", $user_id);
$stmt_toko->execute();
$toko_result = $stmt_toko->get_result();
if ($toko_result->num_rows === 0) {
    echo '<div class="alert alert-danger">Profil toko Anda tidak ditemukan.</div>';
        require_once '../includes/footer_penjual.php';
            exit();
            }
            $toko = $toko_result->fetch_assoc();
            $toko_id = $toko['id'];
            $stmt_toko->close();

            // --- Logika Filter ---
            $filter_start_date = $_GET['start_date'] ?? '';
            $filter_end_date = $_GET['end_date'] ?? '';

            // --- Klausa WHERE untuk filter periode ---
            $where_periode = "";
            $bind_periode_params = [$toko_id]; // Selalu filter berdasarkan toko_id
            $bind_periode_types = "i";

            if (!empty($filter_start_date)) {
                $where_periode .= " AND tanggal_transaksi >= ?";
                    $bind_periode_params[] = $filter_start_date . " 00:00:00";
                        $bind_periode_types .= "s";
                        }
                        if (!empty($filter_end_date)) {
                            $where_periode .= " AND tanggal_transaksi <= ?";
                                $bind_periode_params[] = $filter_end_date . " 23:59:59";
                                    $bind_periode_types .= "s";
                                    }

                                    // --- Logika untuk Statistik di Card Atas (Dinamis Sesuai Filter) ---

                                    // 1. Saldo Saat Ini (tidak terpengaruh filter)
                                    $saldo_res = $koneksi->prepare("SELECT saldo FROM toko WHERE id = ?");
                                    $saldo_res->bind_param("i", $toko_id);
                                    $saldo_res->execute();
                                    $saldo_saat_ini = (float)$saldo_res->get_result()->fetch_assoc()['saldo'];
                                    $saldo_res->close();

                                    // 2. Total Pemasukan (berdasarkan periode filter)
                                    $pemasukan_query = "SELECT SUM(jumlah) as total FROM riwayat_transaksi_penjual WHERE toko_id = ? AND jenis_transaksi = 'masuk' $where_periode";
                                    $stmt_pemasukan = $koneksi->prepare($pemasukan_query);
                                    // Re-build bind params for this specific query
                                    $pemasukan_params = $bind_periode_params;
                                    $pemasukan_types = $bind_periode_types;
                                    $stmt_pemasukan->bind_param($pemasukan_types, ...$pemasukan_params);
                                    $stmt_pemasukan->execute();
                                    $total_pemasukan = (float)$stmt_pemasukan->get_result()->fetch_assoc()['total'];
                                    $stmt_pemasukan->close();

                                    // 3. Total Pengeluaran (berdasarkan periode filter)
                                    $pengeluaran_query = "SELECT SUM(jumlah) as total FROM riwayat_transaksi_penjual WHERE toko_id = ? AND jenis_transaksi = 'keluar' $where_periode";
                                    $stmt_pengeluaran = $koneksi->prepare($pengeluaran_query);
                                    // Re-build bind params for this specific query
                                    $pengeluaran_params = $bind_periode_params;
                                    $pengeluaran_types = $bind_periode_types;
                                    $stmt_pengeluaran->bind_param($pengeluaran_types, ...$pengeluaran_params);
                                    $stmt_pengeluaran->execute();
                                    $total_pengeluaran = (float)$stmt_pengeluaran->get_result()->fetch_assoc()['total'];
                                    $stmt_pengeluaran->close();


                                    // --- Logika untuk Tabel Riwayat (Dengan Filter) ---
                                    $query_riwayat = "SELECT * FROM riwayat_transaksi_penjual WHERE toko_id = ? $where_periode ORDER BY tanggal_transaksi DESC";
                                    $stmt_riwayat = $koneksi->prepare($query_riwayat);
                                    $stmt_riwayat->bind_param($bind_periode_types, ...$bind_periode_params);
                                    $stmt_riwayat->execute();
                                    $result_riwayat = $stmt_riwayat->get_result();


                                    $periode_teks = "Keseluruhan";
                                    if (!empty($filter_start_date) || !empty($filter_end_date)) {
                                        $start = !empty($filter_start_date) ? date('d/m/Y', strtotime($filter_start_date)) : '...';
                                            $end = !empty($filter_end_date) ? date('d/m/Y', strtotime($filter_end_date)) : '...';
                                                $periode_teks = "$start - $end";
                                                }
