<?php
// /admin/penarikan_dana.php (Versi Final Lengkap)

// Aktifkan error reporting untuk debugging selama pengembangan
ini_set('display_errors', 1);
error_reporting(E_ALL);

// Bagian 1: Logika Pemrosesan Form
// Cek jika ada permintaan POST untuk memperbarui status penarikan
if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['update_penarikan'])) {
    // Pastikan file konfigurasi sudah dimuat untuk koneksi database dan session
        require_once dirname(__DIR__) . '/config/database.php'; 

            $id = (int)$_POST['id']; // ID penarikan_dana
                $status = $_POST['status']; // Status baru: 'selesai' atau 'ditolak'
                    $admin_user_id = $_SESSION['user_id'] ?? 0; // Ambil ID admin yang sedang login

                        // Mulai transaksi database untuk memastikan semua query berhasil atau semua dibatalkan
                            mysqli_begin_transaction($koneksi);

                                try {
                                        // Ambil jumlah penarikan dan toko_id dari tabel `penarikan_dana` yang statusnya masih 'pending'
                                                $stmt_get_penarikan = $koneksi->prepare("SELECT jumlah, toko_id FROM penarikan_dana WHERE id = ? AND status = 'pending'");
                                                        $stmt_get_penarikan->bind_param("i", $id);
                                                                $stmt_get_penarikan->execute();
                                                                        $penarikan_data = $stmt_get_penarikan->get_result()->fetch_assoc();
                                                                                
                                                                                        if (!$penarikan_data) {
                                                                                                     throw new Exception("Permintaan penarikan tidak ditemukan atau sudah diproses sebelumnya.");
                                                                                                             }
                                                                                                                     
                                                                                                                             $jumlah_penarikan = (float)$penarikan_data['jumlah']; 
                                                                                                                                     $toko_id_penarikan = (int)$penarikan_data['toko_id'];
                                                                                                                                             $stmt_get_penarikan->close();
                                                                                                                                                     
                                                                                                                                                             // Ambil nama toko untuk deskripsi log
                                                                                                                                                                     $nama_toko_res = $koneksi->query("SELECT nama_toko FROM toko WHERE id = $toko_id_penarikan");
                                                                                                                                                                             $nama_toko = $nama_toko_res->fetch_assoc()['nama_toko'] ?? 'Toko Tidak Dikenal';
                                                                                                                                                                                     
                                                                                                                                                                                             // 1. Update status di tabel `penarikan_dana`
                                                                                                                                                                                                     $stmt_update = $koneksi->prepare("UPDATE penarikan_dana SET status = ?, tanggal_proses = NOW() WHERE id = ?");
                                                                                                                                                                                                             $stmt_update->bind_param("si", $status, $id);
                                                                                                                                                                                                                     if (!$stmt_update->execute()) {
                                                                                                                                                                                                                                 throw new Exception("Gagal memperbarui status penarikan.");
                                                                                                                                                                                                                                         }
                                                                                                                                                                                                                                                 $stmt_update->close();
                                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                                                 $jenis_transaksi_admin = '';
                                                                                                                                                                                                                                                                         $deskripsi_admin = '';

                                                                                                                                                                                                                                                                                 if ($status == 'selesai') {
                                                                                                                                                                                                                                                                                             // 2a. Kurangi saldo admin di tabel `pengaturan`
                                                                                                                                                                                                                                                                                                         $stmt_kurangi_saldo = $koneksi->prepare("UPDATE pengaturan SET nilai_pengaturan = nilai_pengaturan - ? WHERE nama_pengaturan = 'saldo_admin'");
                                                                                                                                                                                                                                                                                                                     $stmt_kurangi_saldo->bind_param("d", $jumlah_penarikan);
                                                                                                                                                                                                                                                                                                                                 if (!$stmt_kurangi_saldo->execute()) {
                                                                                                                                                                                                                                                                                                                                                 throw new Exception("Gagal mengurangi saldo admin.");
                                                                                                                                                                                                                                                                                                                                                             }
                                                                                                                                                                                                                                                                                                                                                                         $stmt_kurangi_saldo->close();

                                                                                                                                                                                                                                                                                                                                                                                     // Siapkan data untuk log transaksi admin
                                                                                                                                                                                                                                                                                                                                                                                                 $jenis_transaksi_admin = 'penarikan_seller_selesai';
                                                                                                                                                                                                                                                                                                                                                                                                             $deskripsi_admin = "Transfer dana ke {$nama_toko} (Penarikan #${id})";

                                                                                                                                                                                                                                                                                                                                                                                                                     } elseif ($status == 'ditolak') {
                                                                                                                                                                                                                                                                                                                                                                                                                                 // 2b. Kembalikan dana ke saldo toko
                                                                                                                                                                                                                                                                                                                                                                                                                                             $stmt_kembalikan_saldo = $koneksi->prepare("UPDATE toko SET saldo = saldo + ? WHERE id = ?");
                                                                                                                                                                                                                                                                                                                                                                                                                                                         $stmt_kembalikan_saldo->bind_param("di", $jumlah_penarikan, $toko_id_penarikan);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if (!$stmt_kembalikan_saldo->execute()) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     throw new Exception("Gagal mengembalikan saldo ke toko.");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $stmt_kembalikan_saldo->close();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     // Catat di riwayat keuangan penjual bahwa dana dikembalikan
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 $deskripsi_log_penjual = "Penarikan Dana #${id} ditolak. Dana dikembalikan ke saldo.";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $stmt_log_penjual = $koneksi->prepare("INSERT INTO riwayat_transaksi_penjual (toko_id, penarikan_id, jenis_transaksi, jumlah, deskripsi) VALUES (?, ?, 'masuk', ?, ?)");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         $stmt_log_penjual->bind_param("iids", $toko_id_penarikan, $id, $jumlah_penarikan, $deskripsi_log_penjual);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     $stmt_log_penjual->execute();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 $stmt_log_penjual->close();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         // 3. Catat aksi admin ke `riwayat_transaksi_admin` (hanya jika penarikan disetujui)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 if (!empty($jenis_transaksi_admin)) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $stmt_log_admin = $koneksi->prepare("INSERT INTO riwayat_transaksi_admin (admin_user_id, jenis_transaksi, referensi_id, jumlah, deskripsi) VALUES (?, ?, ?, ?, ?)");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         $stmt_log_admin->bind_param("isids", $admin_user_id, $jenis_transaksi_admin, $id, $jumlah_penarikan, $deskripsi_admin);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if (!$stmt_log_admin->execute()) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     throw new Exception("Gagal mencatat riwayat transaksi admin.");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $stmt_log_admin->close();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     mysqli_commit($koneksi);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             header("Location: penarikan_dana.php?status_update=sukses");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     exit();

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         } catch (Exception $e) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 mysqli_rollback($koneksi);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         error_log("Error updating penarikan_dana: " . $e->getMessage());
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 header("Location: penarikan_dana.php?status_update=gagal&pesan=" . urlencode($e->getMessage()));
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         exit();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             // Bagian 2: Tampilan Halaman
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             // Pastikan koneksi dan header dipanggil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             require_once '../config/database.php';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $page_title = "Manajemen Penarikan Dana";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             require_once '../includes/header_admin.php'; 

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             // Query untuk mengambil semua data penarikan
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $query = "
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 SELECT pd.*, t.nama_toko, t.nama_bank, t.nomor_rekening, t.nama_pemilik_rekening
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     FROM penarikan_dana pd
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         JOIN toko t ON pd.toko_id = t.id
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ORDER BY pd.tanggal_request DESC
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             $result = mysqli_query($koneksi, $query);

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if ($result === false) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 error_log("Error fetching penarikan_dana: " . mysqli_error($koneksi));
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     echo '<div class="alert alert-danger">Gagal memuat data penarikan dana.</div>';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ?>

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     <h1>Manajemen Penarikan Dana</h1>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     <p>Kelola permintaan penarikan dana dari penjual. Dana yang disetujui akan mengurangi Saldo Admin.</p>

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     <?php
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if (isset($_GET['status_update'])) {
