// Ambil semua produk dari toko ini, termasuk kolom promo baru
$query_produk = "SELECT id, nama_produk, harga, harga_diskon, promo_mulai, promo_akhir, stok, gambar_produk, status_moderasi FROM produk WHERE toko_id = $toko_id ORDER BY created_at DESC";
