<?php
// /admin/saldo_negatif.php (Versi Penuh dan Lengkap)
$page_title = "Pantau Saldo Negatif Penjual";
require_once '../includes/header_admin.php';

// Query untuk mengambil semua toko dengan saldo di bawah nol,
// digabungkan dengan data pengguna untuk informasi kontak.
$query_toko_negatif = "
    SELECT 
            t.id AS toko_id,
                    t.nama_toko,
                            t.saldo,
                                    u.id AS user_id,
                                            u.nama_lengkap AS nama_pemilik,
                                                    u.email AS email_pemilik,
                                                            u.is_active AS status_akun_pengguna
                                                                FROM 
                                                                        toko t
                                                                            JOIN 
                                                                                    users u ON t.user_id = u.id
                                                                                        WHERE 
                                                                                                t.saldo < 0
                                                                                                    ORDER BY 
                                                                                                            t.saldo ASC
                                                                                                            ";
                                                                                                            $result = $koneksi->query($query_toko_negatif);
                                                                                                            ?>

